// MySQL Dolphin Icon (Classic MySQL dolphin logo)
export function MySQLIcon({ className }: IconProps) {
  return (
    <img
      src="/assets/icons/mysql-icon.webp"
      alt="MySQL"
      className={cn('w-4 h-4', className)}
    />
  )
}

