// Generic Database Icon (fallback)
export function GenericDatabaseIcon({ className }: IconProps) {
  return (
    <svg
      viewBox="0 0 24 24"
      className={cn('w-4 h-4', className)}
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <ellipse cx="12" cy="5" rx="9" ry="3" />
      <path d="M21 12c0 1.66-4 3-9 3s-9-1.34-9-3" />
      <path d="M3 5v14c0 1.66 4 3 9 3s9-1.34 9-3V5" />
    </svg>
  )
}

// Get database icon by type
export function getDatabaseIcon(dbType: string): React.ComponentType<IconProps> {
  const type = dbType.toLowerCase()
  switch (type) {
    case 'mysql':
      return MySQLIcon
    case 'mariadb':
      return MariaDBIcon
    case 'postgresql':
    case 'postgres':
      return PostgreSQLIcon
    case 'sqlite':
      return SQLiteIcon
    default:
      return GenericDatabaseIcon
  }
}
